mapper.update:
update sys_user
set last_login_time=#{lastLoginTime}
where user_name=#{userName}

update sys_user
set pswd=#{newPwd}
where user_name=#{userName} and pswd=#{oldPwd}

mapper.select:
select a.id,a.user_name,a.real_name,a.email,a.pswd,a.phone,a.create_time,a.last_login_time,a.user_status,c.id as roleId, c.name as roleName, c.code as roleCode
from sys_user a, sys_user_role b, sys_role c
where  a.id=b.uid and b.rid=c.id and a.user_name=#{userName}

mapper.sql:
from sys_user a, sys_user_role b, sys_role c

    a.id=b.uid and b.rid=c.id

        AND a.id = #{userId}


        AND a.real_name = #{realName}


        AND c.app_id = #{appId}
